# Configure logging
logging.basicConfig(level=logging.INFO)

# Initialize bot and dispatcher
bot = Bot(token=TOKEN)
dp = Dispatcher(bot)


def get_vault_capacity_ceth():
    """Function to get the current ETH call vault capacity"""
    contract = w3.eth.contract(address=CETH_ADDRESS, abi=CETH_ABI)

    cap = contract.functions.cap().call()
    balance = contract.functions.totalBalance().call()

    capacity = (cap - balance) / 10 ** 18
    time.sleep(0.1)
    # print(f"{capacity:.2f} eth")
    return capacity


def get_vault_capacity_cbtc():
    """Function to get the current BTC call vault capacity"""
    contract = w3.eth.contract(address=CBTC_ADDRESS, abi=CBTC_ABI)

    cap = contract.functions.cap().call()
    balance = contract.functions.totalBalance().call()

    capacity = (cap - balance) / 10 ** 8
    time.sleep(0.1)
    # print(f"{capacity:.2f} eth")
    return capacity


def get_vault_capacity_peth():
    """Function to get the current ETH put vault capacity"""
    contract = w3.eth.contract(address=PETH_ADDRESS, abi=PETH_ABI)

    cap = contract.functions.cap().call()
    balance = contract.functions.totalBalance().call()

    capacity = (cap - balance) / 10 ** 6
    time.sleep(0.1)
    # print(f"{capacity:.2f} eth")
    return capacity


@dp.message_handler(commands=['start'])
async def send_welcome(message: types.Message):
    await message.answer("🎀 Hello welcome to the Ribbon Finance Vault Bot 🎀")


@dp.message_handler(commands=['run'])
async def subscribe(message: types.Message):
    while True:
        # get all vault capacities
        ceth_vault_status = get_vault_capacity_ceth()
        cbtc_vault_status = get_vault_capacity_cbtc()
        peth_vault_status = get_vault_capacity_peth()

        # create blank message
        msg = ""

        # if the ETH call vault capacity is above the threshold add text to message
        if ceth_vault_status > float(CETH_CAPACITY_THRESHOLD):
            msg = msg + f"🎀 Current T-ETH-C Capacity " + '{:,.2f}'.format(ceth_vault_status) + " ETH"

        # if the BTC call vault capacity is above the threshold add text to message
        if cbtc_vault_status > float(CBTC_CAPACITY_THRESHOLD):
            if msg != "":
                msg = msg + '\n'
            msg = msg + f"🎀 Current T-WBTC-C Capacity " + '{:,.2f}'.format(cbtc_vault_status) + " BTC"

        # if the ETH put vault capacity is above the threshold add text to message
        if peth_vault_status > float(PETH_CAPACITY_THRESHOLD):
            if msg != "":
                msg = msg + '\n'
            msg = msg + f"🎀 Current T-USDC-P-ETH Capacity " + '${:,.2f}'.format(peth_vault_status) + " USDC"

        # if message is not blank send to channel
        if msg != "":
            await message.answer(msg)

            # sleep then repeat command
            await asyncio.sleep(VAULT_REFRESH_TIMER)

if __name__ == '__main__':
    executor.start_polling(dp, skip_updates=True)
